mapper.select:
select book_id, book_name, author, isbn
from books
where book_id = #{bookId}

select book_id, book_name, author, isbn
from books
where book_name = #{bookName}

select book_id, book_name, author, isbn
from books
where isbn = #{isbn}

select book_id, book_name, author, isbn
from books
limit #{limit} offset #{offset}

mapper.update:
update books
set book_name = #{bookName}, author = #{author}, isbn = #{isbn}, rented = #{rented}
where book_id = #{bookId}
