mapper.select:
select rental_id, book_id, user_id, rented_at, returned_at
from rentals
where user_id = #{userId}

select rental_id, book_id, user_id, rented_at, returned_at
from rentals
where rental_id = #{rentalId}

mapper.update:
update rentals
set book_id = #{bookId}, user_id = #{userId}, rented_at = #{rentedAt}, returned_at = #{returnedAt}
where rental_id = #{rentalId}
